-(instancetype)init
{
    self = [super init];
    if (self)
    {
      static dispatch_once_t httpOnceToken;
      dispatch_once(&httpOnceToken, ^{
      queue =  [[NSOperationQueue alloc]init];
      NSString *cerString=@"    ff78555102fd39499bc093b5b94903d2c72fa1cf2a73eb33c267935a963ca6660370e270ec3baab02017fd218af397632592cd595c0f6ca7d7fb6dc2d54ce33cd1889e15";
